export type BaseFlowState<StepType, SuccessDataType, ErrorDetailType> = {
    currentStep: StepType | null;
    isLoading: boolean;
    attempts: number;
    maxAttempts: number;
    errorDetails: ErrorDetailType | null;
    successData: SuccessDataType | null;
    flowOutcome: 'success' | 'error' | 'pending' | null;
    sessionToken: string | null;
};
