import type { BaseFlowState } from '../../../FlowManager/BaseFlowState';
import type { CaptureIdResponse } from '../capture/Api';
export type OnboardingError = 'NoMoreTries' | 'SessionError' | 'UploadError' | 'PermissionDenied' | 'InconsistentFlowConfiguration';
export type IdFlowStep = 'chooser' | 'id' | 'passport' | 'permissions' | 'end' | 'error';
export type IdSuccessData = CaptureIdResponse & {
    transactionId?: string;
};
export type IdErrorDetail = {
    message: string;
    code?: OnboardingError | string;
    [key: string]: any;
};
export type IdFlowConcreteState = BaseFlowState<IdFlowStep, IdSuccessData, IdErrorDetail>;
